    public int longestValidParentheses(String s) {

        int len = s.length();
        int left = 0;
        int right = 0;
        int result = 0;
        if(len == 0) {
            return result;
        }
        for(int i=0;i<len;i++) {
            if(s.charAt(i) == '(') {
                left++;
            } else {
                right++;
            }
            if(left == right ) {
                result = Math.max(result,2*left);
            }
            if(left<right) {
                left = right = 0;
            }
        }
        left = right = 0;
        for(int i=len-1;i>=0;i--) {
            if(s.charAt(i) == '(') {
                left++;
            } else {
                right++;
            }
            if(left==right) {
                result = Math.max(result, 2 * left);

            }
            if(left>right) {
                left = right = 0;
            }
        }
        return result;
    }
